graphql.query:
query SupplierProducts {
  supplierProducts(first: 2) {
    edges {
      node {
        id
        suppliers (first: 1){
          edges {
            node {
              id
              name
            }
          }
        }
        products(first: 1) {
          edges {
            node {
              id
              name
            }
          }
        }
      }
    }
  }
}

query StoreProducts {
  storeProducts(first: 2) {
    edges {
      node {
        id
        stores (first: 1){
          edges {
            node {
              id
              name
            }
          }
        }
        products(first: 1) {
          edges {
            node {
              id
              name
            }
          }
        }
      }
    }
  }
}

query PurchasedProducts {
  purchasedProducts(first: 2) {
    pageInfo {
      hasNextPage
      endCursor
    }
    edges {
      node {
        id   
        discount
        price
        createdAt
        product(first: 5) {
          pageInfo {
            hasPreviousPage
          }
          edges {
            node {
              category
              id 
              name
              amount
              createdAt
            }
          }
        }
      }
    }
  }
}

query Address {
  addresses (first:10){
    edges {
      node {
        id
        address1
        address2
        suppliers {
          edges {
            node{
              id
              name
            }
          }
        }
        stores {
          edges {
            node {
              id
              name
            }
          }
        }
      }
    }
  }
}

query Suppliers {
  suppliers (first:10){
    edges {
      node {
        id
        name
        addressId
        address {
          edges {
            node {
              id
              address1
            }
          }
        }
        products {
          edges {
            node {
              id
              name
            }
          }
        }
      }
    }
  }
}

query Stores {
  stores (first:10){
    edges {
      node {
        id
        name
        addressId
        address {
          edges {
            node {
              id
              address1
            }
          }
        }
        products {
          edges {
            node {
              id
              name
            }
          }
        }
      }
    }
  }
}

query Products {
  products(first: 10) {
    edges {
      node {
        id
        name
        category
        amount
        stores {
          edges {
            node {
              name
              id
            }
          }
        }
        suppliers {
          edges {
            node {
              id
              name
            }
          }
        }
      }
    }
  }
}
